definition.script:
pipeline{
    agent{
        label 'master'
    }
    tools {
        jfrog 'my-jfrog-cli'
        gradle 'my-gradle'
    }
    environment {
        GIT_REPO_URL = 'http://gitlab:13000/root/webapp-webapi.git'
        JAR_FILE_PATTERN = 'build/libs/apisl.handson.grafana.webapp.webapi-*-SNAPSHOT.jar'
        ARTIFACTORY_URL = 'http://artifactory:8081/artifactory'
        ARTIFACTORY_REPO = 'hands-on-webapp-webapi'
        SBOM_FILE_PATH = 'build/reports/application.cdx.json'
        DEPENDENCY_TRACK_API_KEY_ID = 'dependency-track-api-key'
    }
    stages{
//        stage("Clean up a local environment"){
//            steps{
//                echo "### START: clean up the cloned directoy"
//                deleteDir()
//            }
//        }
        stage("Checkout a repository from GitLab"){
            steps{
               echo "### START: clone a repository from GitLab"
               git branch: 'main', url: "${env.GIT_REPO_URL}"
            }
        }
        stage('Build with JFrog CLI') {
            steps {
                echo "### START: build with jfrog cli"
                jf 'gradle-config --repo-deploy ${ARTIFACTORY_REPO}'
                jf "rt gradle build -x test -x check --info --build-name=${ARTIFACTORY_REPO} --build-number=${BUILD_NUMBER}"
            }
        }
        stage("Run Unit Tests"){
            steps{
                echo "### START: run unit tests"
                sh "./gradlew test -x check --info"
            }
            post {
                always {
                    junit 'build/test-results/test/*.xml'
                }
            }
        }
        stage("Analyze test codes coverage"){
            steps{
                echo "### START: analyze a test code coverage"
                sh "./gradlew jacocoTestReport --info"
            }
            post {
                always {
                    recordCoverage(
                        tools: [[parser: 'JACOCO', pattern: 'build/reports/jacoco/test/jacocoTestReport.xml']],
                        id: 'jacoco', name: 'JaCoCo Coverage',
                        sourceCodeRetention: 'EVERY_BUILD',
                        qualityGates: [
                            [threshold: 60.0, metric: 'LINE', baseline: 'PROJECT', unstable: true],
                            [threshold: 60.0, metric: 'BRANCH', baseline: 'PROJECT', unstable: true]]
                    )
                }
            }
        }
        stage("Analyze codes with Checkstyle"){
            steps{
                echo "### START: analyze codes with checkstyle"
                sh "./gradlew checkstyleMain checkstyleTest --info"
            }
            post {
                always {
                    recordIssues tools: [
                        checkStyle(pattern: 'build/reports/checkstyle/')
                    ]
                }
            }
        }
        stage("Analyze codes with PMD"){
            steps{
                echo "### START: analyze codes with pmd"
                sh "./gradlew pmdMain pmdTest --info"
            }
            post {
                always {
                    recordIssues tools: [
                        pmdParser(pattern: 'build/reports/pmd/*.xml')
                    ]
                }
            }
        }
        stage('Generate SBOM') {
            steps {
                echo "### START: generating SBOM using CycloneDX plugin"
                sh './gradlew cyclonedxBom --info'
            }
        }
        stage('Publish SBOM to Dependency-Track') {
            steps {
                withCredentials([string(credentialsId: "${env.DEPENDENCY_TRACK_API_KEY_ID}", variable: 'API_KEY')]) {
                    dependencyTrackPublisher artifact: "${env.SBOM_FILE_PATH}",
                    projectName: "${env.ARTIFACTORY_REPO}",
                    projectVersion: "${env.BUILD_NUMBER}",
                    synchronous: true,
                    dependencyTrackApiKey: API_KEY
                }
            }
        }
        stage('Generate a Javadoc') {
            steps {
                echo "### START: generate a javadoc"
                sh './gradlew javadoc'
            }
        }
        stage('Publish a Javadoc') {
            steps {
                echo "### START: publish a javadoc using javadoc plugin"
                javadoc javadocDir: 'build/docs/javadoc', keepAll: true
            }
        }
        stage('Release an artifact to Artifactory') {
            steps {
                echo "### START: release an artifact to artifactory"
                jf 'rt u ${JAR_FILE_PATTERN} ${ARTIFACTORY_REPO}'
            }
        }
        stage('Publish a build info to Artifactory') {
            steps {
                echo "### START: publish a build info to Artifactory"
                jf "rt build-publish ${ARTIFACTORY_REPO} ${BUILD_NUMBER}"
            }
        }
    }
    post{
        always{
            echo "========always========"
        }
        success{
            echo "========pipeline executed successfully ========"
        }
        failure{
            echo "========pipeline execution failed========"
        }
    }
}
